/**
  * @brief  Initializes the SD card device.
  * @retval SD status
  */
/*
 * Merged custom BSP SDIO implementation
 * - Keep signature (uint8_t) required by FatFs middleware (MSD_OK/MSD_ERROR)
 * - Reuse HAL and hsd from core
 */
uint8_t BSP_SD_Init(void)
{
  uint8_t sd_state = MSD_OK;
  /* USER CODE BEGIN BSP_SD_Init_Debug */
  /* User diagnostics: kept inside USER CODE to avoid being overwritten by CubeMX */
  /* Example: prints card presence and initial sd_state */
  printf("[BSP_SD] ===== BSP SD INIT =====\r\n");
  printf("[BSP_SD] enter, initial sd_state=%u\r\n", sd_state);
  /* USER CODE END BSP_SD_Init_Debug */
  /* Check if the SD card is plugged in the slot */
  if (BSP_SD_IsDetected() != SD_PRESENT)
  {
    /* USER CODE BEGIN BSP_SD_NotDetected */
  printf("[BSP_SD] card not detected (BSP_SD_IsDetected != SD_PRESENT)\r\n");
    /* USER CODE END BSP_SD_NotDetected */
    return MSD_ERROR;
  }
  /* HAL SD initialization */
  if (HAL_SD_Init(&hsd) == HAL_OK)
  {
    sd_state = MSD_OK;
  printf("[BSP_SD] HAL_SD_Init returned MSD_OK\r\n");
    /* Wait for card to reach transfer state (give it up to 500 ms) */
    {
      uint32_t t0 = 0;
      uint32_t tmax = 500;
      while (t0 < tmax)
      {
        if (HAL_SD_GetCardState(&hsd) == HAL_SD_CARD_TRANSFER)
        {
          break;
        }
        HAL_Delay(10);
        t0 += 10;
      }
      if (t0 >= tmax)
      {
  printf("[BSP_SD] card did not enter TRANSFER state after %lu ms\r\n", (unsigned long)tmax);
        sd_state = MSD_ERROR;
      }
    }
    /* Print card info for diagnostics */
    HAL_SD_CardInfoTypeDef cardInfo;
    HAL_SD_GetCardInfo(&hsd, &cardInfo);
    printf("[BSP_SD] CardInfo: CardType=%lu, CardVersion=%lu, Class=%lu, RelCardAdd=%lu\r\n",
      (unsigned long)cardInfo.CardType,
      (unsigned long)cardInfo.CardVersion,
      (unsigned long)cardInfo.Class,
      (unsigned long)cardInfo.RelCardAdd);
    printf("[BSP_SD] CardInfo cont: BlockNbr=%lu, BlockSize=%lu, LogBlockNbr=%lu, LogBlockSize=%lu\r\n",
      (unsigned long)cardInfo.BlockNbr,
      (unsigned long)cardInfo.BlockSize,
      (unsigned long)cardInfo.LogBlockNbr,
      (unsigned long)cardInfo.LogBlockSize);
  }
  else
  {
  printf("[BSP_SD] HAL_SD_Init returned ERROR (err=0x%08lX)\r\n", (unsigned long)hsd.ErrorCode);
  /* print HAL SD card state for more diagnostics */
  printf("[BSP_SD] HAL_SD_GetCardState() = %d\r\n", HAL_SD_GetCardState(&hsd));
    sd_state = MSD_ERROR;
  }
  /* Configure SD Bus width (4 bits mode selected) */
  if (sd_state == MSD_OK)
  {
    /* Give card short time to enter transfer state */
    HAL_Delay(50);

    /* Try to enable 4-bit operation */
    HAL_StatusTypeDef wide_ret = HAL_SD_ConfigWideBusOperation(&hsd, SDIO_BUS_WIDE_4B);
    if (wide_ret == HAL_OK)
    {
  printf("[BSP_SD] HAL_SD_ConfigWideBusOperation 4-bit OK\r\n");
    }
    else
    {
  printf("[BSP_SD] HAL_SD_ConfigWideBusOperation 4-bit failed (%d), trying 1-bit fallback\r\n", (int)wide_ret);
      /* Try fallback to 1-bit */
      wide_ret = HAL_SD_ConfigWideBusOperation(&hsd, SDIO_BUS_WIDE_1B);
      if (wide_ret == HAL_OK)
      {
  printf("[BSP_SD] HAL_SD_ConfigWideBusOperation fallback to 1-bit OK\r\n");
      }
      else
      {
  printf("[BSP_SD] HAL_SD_ConfigWideBusOperation fallback failed (%d)\r\n", (int)wide_ret);
        sd_state = MSD_ERROR;
      }
    }
  }

  printf("[BSP_SD] final sd_state = %u\r\n", sd_state);

  return sd_state;
}
